import express from 'express';
import connect from './db.js';
import cors from 'cors';
import mongo from 'mongodb'
import axios from 'axios'


const app = express();
const port = 4200

app.use(cors());
app.use(express.json());

app.listen(port, () => console.log(`Port ${port} na slušanju!`))



app.post('/products', async (req,res)=>{
    let db = await connect();
    
    let posts = req.body;
    
    let result = await db.collection('posts').insertOne(posts);
   
    if (result.insertedCount == 1) {
        res.send({
            status: 'success',
            id: result.insertedId,
        });
    } 
    else {
        res.send({
            status: 'fail',
        });
    }
})

app.get ('/products', async (req , res) => {
    try {
            const db = await connect();
            const cursor = await db.collection('posts').find({});
            const results = await cursor.toArray();
    
    
    res.json(results);
    if (results.insertedCount == 1) {
        res.send({
            status: 'success',
            id: results.insertedId,
        });
    } 
    else {
        res.send({
            status: 'fail',
        });
    }
    /*axios.post('http://localhost:4205/events', {
        type: 'Success',
        data: {
            _id: mongo.ObjectID(id),
            results: req.body,
            postId: req.params.id,
            
        }
       
    });*/
    return res.status(200).send();
    } catch(error){
        console.log(error);
    }
});


app.get ('/products/:id', async (req , res) => {
    
    let id = req.params.id;
    let db = await connect();
    console.log("params: ",id);
    
    let doc = await db.collection("posts").findOne({_id: mongo.ObjectID(id)});
    console.log(doc)
    res.json(doc)

    /*await axios.get('http://localhost:4205/events', {
        type: 'Success',
        data: {
            _id: mongo.ObjectID(id),
            doc,
            postId: req.params.id,
        }
    })*/
});

